let QUERY_ALL_CONTACT = encodeURIComponent(
    '*[_type == "contact"]{\n  ...,\n  \n  } '
  ),
  URL_to_get_contact = `https://${PROJECT_ID}.api.sanity.io/v2021-10-21/data/query/${DATASET}?query=${QUERY_ALL_CONTACT}`;
const getContact = () => {
  fetch(URL_to_get_contact)
    .then((e) => e.json())
    .then(({ result: e }) => {
      if (e.length > 0) {
        const t = document.getElementById("contact-col");
        t &&
          e.forEach((e) => {
            const n = e?.street,
              c = document.createElement("p");
            (c.className = "address"),
              (c.innerHTML = n ? `ul. ${e.street}` : ""),
              t.appendChild(c);
            const a = e?.city,
              o = document.createElement("p");
            (o.className = "address"),
              (o.innerHTML = a ? ` ${e.city}` : ""),
              t.appendChild(o);
            const l = e?.phone,
              s = document.createElement("p");
            (s.className = "footer-link"),
              (s.innerHTML = l ? `Tel: ${e.phone}` : ""),
              t.appendChild(s);
            const d = e?.email,
              i = document.createElement("p");
            (i.className = "footer-link"),
              (i.innerHTML = d ? `E-mail: ${e.email}` : ""),
              t.appendChild(i);
          });
      }
    });
};
