let QUERY_ALL_STATES = encodeURIComponent(
    '*[_type == "plate" && isPromo != true && country == "Stany Zjednoczone"] {\n    state,\n  }  | order(state)'
  ),
  URL_to_get_states = `https://${PROJECT_ID}.api.sanity.io/v2021-10-21/data/query/${DATASET}?query=${QUERY_ALL_STATES}`;
const getStates = () =>
    fetch(URL_to_get_states)
      .then((t) => t.json())
      .then(({ result: t }) => {
        let e = document.getElementById("usa");
        if (t.length > 0) {
          const n = [...new Map(t.map((t) => [t.state, t])).values()];
          e &&
            n.forEach((t) => {
              if (!document.getElementById(`${t.state}`)) {
                const n = document.createElement("li");
                (n.className = "menu-nav-link"),
                  (n.textContent = `${t.state}`),
                  (n.id = `${t.state}`),
                  n.setAttribute("onclick", "filterPlates(this);"),
                  e.appendChild(n);
              }
            });
        }
      })
      .catch((t) => console.error(t)),
  getOtherCountry = async (t) => {
    selectState(""), selectCategory(t?.id), await resetFilters(t?.id);
  };
