const toggleButton = (e) => {
  e.classList.toggle("active");
  let t = e.nextElementSibling;
  t.style.maxHeight
    ? ((t.style.maxHeight = null), selectCategory(""))
    : ((t.style.maxHeight = t.scrollHeight + "px"),
      selectCategory("Stany Zjednoczone"));
  const l = document.documentElement.scrollWidth;
  let n = document.querySelectorAll(".menu-nav-link");
  l < 636 &&
    n.forEach(function (l) {
      l.addEventListener("click", function () {
        e.classList.toggle("active"), (t.style.maxHeight = null);
      });
    });
};
