const toggleButton = (t) => {
  t.classList.toggle("active");
  let e = t.nextElementSibling;
  e.style.maxHeight
    ? ((e.style.maxHeight = null), selectCategory(""))
    : ((e.style.maxHeight = e.scrollHeight + "px"),
      selectCategory("Stany Zjednoczone"));
  const n = document.documentElement.scrollWidth;
  let l = document.querySelectorAll(".menu-nav-link");
  n < 636
    ? l.forEach(function (n) {
        n.addEventListener("click", function () {
          t.classList.toggle("active"), (e.style.maxHeight = null);
        });
      })
    : n > 636 &&
      l.forEach(function (t) {
        t.addEventListener("click", function () {
          window.scrollTo({ top: 0, behavior: "instant" });
        });
      });
};
