  flagButton = document.getElementById("flagButton");
let currentLanguage =
  window.navigator.userLanguage || window.navigator.language;
flagButton.addEventListener("click", () => {
  "pl" === currentLanguage
    ? ((currentLanguage = "cs"),
      flagButton.classList.remove("poland"),
      flagButton.classList.remove("usa"),
      flagButton.classList.add("czech"),
      translation())
    : "cs" === currentLanguage
    ? ((currentLanguage = "en"),
      flagButton.classList.remove("czech"),
      flagButton.classList.remove("poland"),
      flagButton.classList.add("usa"),
      translation())
    : ((currentLanguage = "pl"),
      flagButton.classList.remove("usa"),
      flagButton.classList.remove("czech"),
      flagButton.classList.add("poland"),
      translation());
});
const translation = () => {
  var o;
  (o = currentLanguage),
    document.querySelectorAll("[data-translate]").forEach((t) => {
      const a = t.getAttribute("data-translate");
      translations[o] &&
        translations[o][a] &&
        (t.textContent = translations[o][a]);
    });
};
